import { StatusBar } from "expo-status-bar";
import { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  TouchableOpacity,
} from "react-native";

export default function App() {
  const [valor1, setValor1] = useState(0);
  const [valor2, setValor2] = useState(0);
  const [resultado, setResultado] = useState(0);

  function soma() {
    let r =
      Number.parseFloat(valor1.replace(",", ".")) +
      Number.parseFloat(valor2.replace(",", "."));
    setResultado(r);
  }

  return (
    <View style={styles.container}>
      <Text style={styles.legenda}>Programa que soma valores </Text>

      <Text>Digite o primeiro valor </Text>
      <TextInput
        onChangeText={(texto) => setValor1(texto)}
        style={styles.caixaTexto}
      />

      <Text>Digite o segundo valor </Text>
      <TextInput
        onChangeText={(texto) => setValor2(texto)}
        style={styles.caixaTexto}
      />

      <TouchableOpacity 
        onPress={() => soma()} 
        style={styles.botaoSoma}
      >
        <Text>Soma</Text>
      </TouchableOpacity>

      <Text>Resultado: {resultado.toFixed(2)} </Text>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  legenda: {
    fontSize: 30,
    color: "#f57e42",
    fontWeight: "bold",
  },
  caixaTexto: {
    borderColor: "#000",
    borderRadius: 5,
    borderWidth: 2,
  },
  botaoSoma: {
    width: 150,
    height: 40,
    backgroundColor: "#fc5203",
    borderRadius: 10,
    borderColor: "#d60b22",
  },
});
