import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
    },
  
    legenda: {
      fontSize: 30,
      color: "#f57e42",
      fontWeight: "bold",
    },
    caixaTexto: {
      borderColor: "#000",
      borderRadius: 5,
      borderWidth: 2,
    },
    botaoSoma: {
      marginTop: 10,
      width: 150,
      height: 40,
      backgroundColor: "#fc5203",
      borderRadius: 10,
      borderColor: "#d60b22",
      borderWidth: 1,
      alignItems: "center",
      justifyContent: 'center',
    },
    textoBotao: {
      color: "#FFF",
      fontWeight: "bold",
      fontSize: 20,
    },
  });
  

  export default styles;
